sequential.script:

	var list="@{list}";
	var task="@{task}";
	var attribute_name="@{attribute.name}";
    var splitVars=list.split(",");

    for(var i=0;i<splitVars.length;i++)
    {    
		var oTask=project.createTask(task);
		oTask.init();
		oTask.setDynamicAttribute(attribute_name,splitVars[i]);
		oTask.execute();
	}
