    private void formKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_formKeyPressed
        if(evt.getKeyChar()=='w')
        {
            serialComm.write(80);
            serialComm.write(87);
            serialComm.write(77);
            serialComm.write(170);
            serialComm.write(170);
        }
        else if(evt.getKeyChar()=='s')
        {
            serialComm.write(80);
            serialComm.write(87);
            serialComm.write(77);
            serialComm.write(100);
            serialComm.write(100);
        }
        else if(evt.getKeyChar()=='o')
        {
            serialComm.write(80);
            serialComm.write(87);
            serialComm.write(77);
            serialComm.write(127);
            serialComm.write(170);
        }
        else if(evt.getKeyChar()=='p')
        {
            serialComm.write(80);
            serialComm.write(87);
            serialComm.write(77);
            serialComm.write(170);
            serialComm.write(127);
        }
        else if(evt.getKeyChar()=='z')
        {
            serialComm.write(80);
            serialComm.write(87);
            serialComm.write(77);
            serialComm.write(127);
            serialComm.write(127);
        }
    }//GEN-LAST:event_formKeyPressed
    
